import React from 'react';
import AddProduct from './AddProduct';

const ModalAdd = ({isOpen,onClose}) => {
    const onSaveProduct = async(title,file) =>{ // we can add more params later ie: price
        e.preventDefault();
        const formData = new FormData();
        formData.append("file", file);
        formData.append("title", title);
        try {
            await axios.post("http://localhost:5000/products", formData, {
                headers:{
                    "Content-type":"multipart/form-data"
                }
            });
            navigate("/")
        } catch (error) {
            console.log(error);
        }
      }

  return (
    <div class={isOpen?`modal is-active`:"modal"}>
  <div class="modal-background"></div>
  <div class="modal-card">
    <header class="modal-card-head">
      <p class="modal-card-title">FORM DATA</p>
      <button class="delete" onClick={()=> onClose()} aria-label="close"></button>
    </header>
    <section class="modal-card-body">
        <AddProduct onSaveProduct={onSaveProduct}  />
    </section>
    <footer class="modal-card-foot">
      <button class="button is-success">Save changes</button>
      <button class="button" onClick={()=> onClose()}>Cancel</button>
    </footer>
  </div>
</div>
  )
}

export default ModalAdd
