import React,{useState,useEffect} from 'react';
import axios from "axios";
import {Link} from "react-router-dom";
import ModalAdd from './ModalAdd';
import ConfirmationModal from './ModalDelete'

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [isshowModal,setModals] = useState(false);
    const [selectedProductId,setSelectedProductId]= useState(null)
    const [isShowModalDeleteConfirmation,setIsShowModalDeleteConfirmation] = useState(false)

    useEffect(()=>{
        getProducts();
    },[]);

    const getProducts = async() => {
        const response = await axios.get("http://localhost:5000/products");
        setProducts(response.data);
    }

    async function onDeleteProduct(productId) {
        try {
            await axios.delete(`http://localhost:5000/products/${productId}`);
            getProducts();
        } catch (error) {
            console.log(error);
        }
    }
    function openDeleteConfirmation(productId){
      setIsShowModalDeleteConfirmation(true)
      setSelectedProductId(productId)
    }
  return (
    <div className="container mt-5">
        <button  onClick={()=> setModals(true)} className='button is-success'>Add New</button>
        {isshowModal && <ModalAdd isOpen={isshowModal} onClose={()=> setModals(false)}/>}
        {isShowModalDeleteConfirmation && <ModalAdd isOpen={isshowModal } onDeleteProduct={onDeleteProduct} onClose={()=> setIsShowModalDeleteConfirmation(false)}/>}
        <div className="columns is-multiline">
            {products.map((product)=>(
                <div className="column is-one-quarter" key={product.id}>
                <div className="card">
                  <div className="card-image">
                    <figure className="image is-4by3">
                      <img 
                      src={product.url}
                      alt="Image" />
                    </figure>
                  </div>
                  <div className="card-content">
                    <div className="media">
                      <div className="media-content">
                        <p className="title is-4">{product.name}</p>
                      </div>
                    </div>
                  </div>
                  <footer className='card-footer'>
                    <Link to={`edit/${product.id}`} className="card-footer-item">Edit</Link>
                    <a onClick={()=> openDeleteConfirmation(product.id)} className="card-footer-item">Delete</a>
                  </footer>
                </div>
                
            </div>
            ))}
            
        </div>
    </div>
  )
}

export default ProductList
